CREATE TABLE IF NOT EXISTS "kpi_assess_store" (
        "id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY,
        "title" varchar NOT NULL,
        "standard" varchar NOT NULL,
        "score" varchar NOT NULL,
        "remark" varchar,
        "post_ids" varchar,
        "status" varchar NOT NULL,
        "fixed" bit NOT NULL DEFAULT FALSE,
        "creator" varchar DEFAULT '',
        "create_time" datetime NOT NULL DEFAULT CURRENT_TIMESTAMP,
        "updater" varchar DEFAULT '',
        "update_time" datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
        "deleted" bit NOT NULL DEFAULT FALSE,
        PRIMARY KEY ("id")
    ) COMMENT '考核指标库';

CREATE TABLE IF NOT EXISTS "kpi_assess_issue" (
     "id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY,
     "assess_title" varchar NOT NULL,
     "dept_ids" varchar NOT NULL,
     "post_ids" varchar NOT NULL,
     "assess_start_time" varchar NOT NULL,
     "assess_end_time" varchar NOT NULL,
     "status" varchar NOT NULL,
     "creator" varchar DEFAULT '',
     "create_time" datetime NOT NULL DEFAULT CURRENT_TIMESTAMP,
     "updater" varchar DEFAULT '',
     "update_time" datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
     "deleted" bit NOT NULL DEFAULT FALSE,
     PRIMARY KEY ("id")
    ) COMMENT '考核发布';
CREATE TABLE IF NOT EXISTS "kpi_assess_todolist" (
      "id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY,
      "issue_id" bigint NOT NULL,
      "assess_title" varchar NOT NULL,
      "staff" varchar,
      "staff_time" varchar NOT NULL,
      "staff_status" int NOT NULL,
      "reviewer" varchar,
      "reviewer_time" varchar NOT NULL,
      "reviewer_status" int NOT NULL,
      "decider" varchar,
      "decider_time" varchar NOT NULL,
      "decider_status" int NOT NULL,
      "assess_start_time" varchar NOT NULL,
      "assess_end_time" varchar NOT NULL,
      "status" int NOT NULL,
      "creator" varchar DEFAULT '',
      "create_time" datetime NOT NULL DEFAULT CURRENT_TIMESTAMP,
      "updater" varchar DEFAULT '',
      "update_time" datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
      "deleted" bit NOT NULL DEFAULT FALSE,
      PRIMARY KEY ("id")
    ) COMMENT '考核待办表';

CREATE TABLE IF NOT EXISTS "kpi_assess_staff_item" (
      "id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY,
      "issue_id" bigint NOT NULL,
      "todolist_id" bigint NOT NULL,
      "assess_title" varchar NOT NULL,
      "title" varchar NOT NULL,
      "standard" varchar NOT NULL,
      "score" varchar NOT NULL,
      "staff" varchar NOT NULL,
      "staff_time" varchar,
      "staff_complete_status" varchar NOT NULL,
      "staff_score" varchar NOT NULL,
      "staff_remark" varchar,
      "reviewer" varchar NOT NULL,
      "reviewer_time" varchar,
      "reviewer_complete_status" varchar NOT NULL,
      "reviewer_score" varchar NOT NULL,
      "reviewer_remark" varchar,
      "decider" varchar NOT NULL,
      "decider_time" varchar,
      "decider_complete_status" varchar NOT NULL,
      "decider_score" varchar NOT NULL,
      "decider_remark" varchar,
      "post" varchar NOT NULL,
      "status" varchar NOT NULL,
      "fixed" varchar NOT NULL,
      "creator" varchar DEFAULT '',
      "create_time" datetime NOT NULL DEFAULT CURRENT_TIMESTAMP,
      "updater" varchar DEFAULT '',
      "update_time" datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
      "deleted" bit NOT NULL DEFAULT FALSE,
      PRIMARY KEY ("id")
    ) COMMENT '考核指标库';